function openModal(modalId) {
    document.getElementById(modalId).style.display = "block";
}

function closeModal(modalId) {
    document.getElementById(modalId).style.display = "none";
}

// Language switcher function
function switchLanguage(lang) {
    if (lang === 'ru') {
        document.querySelector('h1').textContent = 'Оценка уровня знаний абитуриента для поступления в колледж!';
        document.querySelector('p').textContent = 'Платформа тестирования, которая позволяет абитуриентам проверять свои способности по специальности для поступления в колледж';
        document.querySelectorAll('.field')[0].placeholder = 'Программное обеспечение';
        document.querySelectorAll('.field')[1].placeholder = 'Переводческое дело';
        document.querySelector('.submit').textContent = 'Начать тест';
        document.querySelectorAll('h2')[0].textContent = 'Программное обеспечение';
        document.querySelectorAll('h2')[1].textContent = 'Переводческое дело';
        document.querySelectorAll('li')[3].textContent = 'Казахский язык';
        document.querySelectorAll('li')[4].textContent = 'Русский язык';
        document.querySelectorAll('li')[5].textContent = 'Английский язык';
    } else {
        document.querySelector('h1').textContent = 'Талапкердің білім деңгейін колледжге түсу үшін бағалау!';
        document.querySelector('p').textContent = 'Талапкерлерге колледжге түсу үшін мамандық бойынша қабілеттерін тексеруге мүмкіндік беретін тестілеу платформасы.';
        document.querySelectorAll('.field')[0].placeholder = 'Бағдарламалық қамтамасыз ету';
        document.querySelectorAll('.field')[1].placeholder = 'Аударма ісі';
        document.querySelector('.submit').textContent = 'Тестілеуді бастау';
        document.querySelectorAll('h2')[0].textContent = 'Бағдарламалық қамтамасыз ету';
        document.querySelectorAll('h2')[1].textContent = 'Аударма ісі';
        document.querySelectorAll('li')[3].textContent = '• Қазақ тілі';
        document.querySelectorAll('li')[4].textContent = '• Орыс тілі';
        document.querySelectorAll('li')[5].textContent = '• Ағылшын тілі';
    }
}

// Close modal when clicking outside
window.onclick = function(event) {
    if (event.target.className === "modal") {
        event.target.style.display = "none";
    }
}

function startTest() {
    window.location.href = '/index.php/main/home'
}